fn main() {    println!("Hello, world!");    formatted_print();}fn formatted_print() {    /* Adding a variable to a println */    println!("{} days!", 31);    /* Using positional variables in println! */    println!("{0} This is {1}. {1} This is {0}", "George", "Thai");    /* using naming conventions for printing */    println!("{subject} {verb} {object}",             object="the lazy dog",             subject="the quick brown fox",             verb="jumps over");    /* using formatting to change a variable to binary */    println!("{} of {:b} people know binary, the other half don't", 1, 2);    /* using width formmating to start 5 spaces indented */    println!("{number:>width$}", number=1, width=5);    /* adding leading zeroes */    println!("{number:>0width$}", number=1, width=6);}